create table animal (
	id INT,
	name VARCHAR(50),
	reg_date DATE,
	species VARCHAR(10),
	breed TEXT,
	color VARCHAR(50),
	gender VARCHAR(6),
	birth_date DATE,
	sterilized VARCHAR(3),
	current_condition VARCHAR(7),
	behavioral_warning TEXT,
	oname VARCHAR(50),
	email VARCHAR(50),
	phone VARCHAR(50),
	address VARCHAR(50)
);
